INSERT INTO `np_goods` VALUES ('549', '1075', '255', '倍舒特棉柔纤巧超长夜用卫生巾350mm 10片', '', '201407090022', '', '219', '', '1', '2014-08-01 12:14:05', '9.50', '1', 'http://192.168.2.99:9999/M00/00/49/wKgCY1O830qAaF4CAAAOZMPyYiE896.jpg', null, '包', '<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">倍舒特棉柔纤巧超长夜用卫生巾10片 SN18010</span><br />\r\n<br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">使用说明：</span><br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">使用时，将产品外包装打开取出其中一片，撕掉背面不干胶，贴到底裤适当的位置，有护翼的位置反粘在底裤上 </span><br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">确保带不干胶那面与底裤紧密贴紧。 </span><br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">每片只能用一次 </span><br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">如肌肤感觉不适，请停止使用，使用后的卫生巾不要丢弃到马桶内 </span><br />\r\n<br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">注意：本品为一次性使用，切勿重复使用，请放置阴凉处。</span><br />\r\n<br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">品牌简介：</span><br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">北京倍舒特企业成立于1998年，是一家专业生产、经营一次性妇幼用品的中外合资企业。销售区域除东北、华北、华东、西北外，还出口到日本、英国、德国、美国、意大利、捷克、斯里兰卡、尼日利亚、印尼等国家。</span><br />\r\n<span style=\"color:#333333;font-family:arial, tahoma, 宋体;background-color:#FFFFFF;\">倍舒特个人护理中心自2003年成立以来，连续8年一直跟踪、了解女性消费者，并以解决女性“漏”的烦恼为使命，倾注了很多心血并做了大量研究。经研究发现，如果只是“瞬间吸收”，消费者在前期使用不会有问题，但使用到一段时间就很有可能出现漏；如果只是“能吸”，消费者有可能在瞬间量大时，出现漏的可能；同时，吸进的经血如果不能被牢牢锁住，人体无规律运动时也很可能出现漏；在倍舒特人不懈的努力下，把这三个方面完美结合的“超吸收”系列产品问世了！倍舒特人可以自豪的告诉女性消费者，“超吸收才是真的防漏”！</span><br />\r\n<br />\r\n<img alt=\"倍舒特棉柔纤巧超长夜用卫生巾10片\" height=\"1489\" width=\"750\" src=\"http://image.suning.cn/images/nrgl/cpjs/103634853_20140504113946.jpg\" /><br />\r\n<img alt=\"倍舒特棉柔纤巧超长夜用卫生巾10片\" height=\"1489\" width=\"750\" src=\"http://image.suning.cn/images/nrgl/cpjs/103634853_20140504113957.jpg\" /><br />\r\n<img alt=\"倍舒特棉柔纤巧超长夜用卫生巾10片\" height=\"1489\" width=\"750\" src=\"http://image.suning.cn/images/nrgl/cpjs/103634853_20140504114008.jpg\" /><br />\r\n<img alt=\"倍舒特棉柔纤巧超长夜用卫生巾10片\" height=\"1489\" width=\"750\" src=\"http://image.suning.cn/images/nrgl/cpjs/103634853_20140504114019.jpg\" /><br />\r\n<img alt=\"倍舒特棉柔纤巧超长夜用卫生巾10片\" height=\"1489\" width=\"750\" src=\"http://image.suning.cn/images/nrgl/cpjs/103634853_20140504114030.jpg\" /><br />\r\n<img alt=\"倍舒特棉柔纤巧超长夜用卫生巾10片\" height=\"1487\" width=\"750\" src=\"http://image.suning.cn/images/nrgl/cpjs/103634853_20140504114040.jpg\" />', '', '', '', '1', '0', '1', 'ningpai ', '2014-07-09 14:20:59', null, '0000-00-00 00:00:00', null, '0000-00-00 00:00:00', '0', '0', 'BOSS', '0', null, null);
